mapper.select:
SELECT num, title, evt_date, exp_date, photo, content
FROM (
	SELECT ROWNUM rnum, tb.*
	FROM (
		SELECT num, title, evt_date, exp_date, photo, content
		FROM event
		WHERE 1=1
		
		ORDER BY num DESC
	) tb WHERE ROWNUM < #{offset} + #{size}
) WHERE rnum > #{offset}

SELECT num, title, evt_date, exp_date, photo, content
FROM Event
WHERE num = #{num}

mapper.update:
UPDATE Event SET title=#{title}, evt_date=#{evt_date}, content=#{content},
    exp_date=#{exp_date}
    
        , photo=#{photo}
    
WHERE num=#{num}
