mapper.select:
SELECT s_FileName
FROM usedBoardFile
WHERE board_num = #{num}

SELECT COUNT(*)
FROM usedBoardReply
WHERE block = 0 AND parent_Num = 0 AND board_num = #{num}

mapper.update:
DELETE FROM usedBoardReply
WHERE num = #{num}

	OR parent_num = #{num}

mapper.select:
SELECT COUNT(*)
FROM usedBoardReply
WHERE block = 0 AND parent_Num = #{num}

SELECT COUNT(*) 
FROM usedBoard 
WHERE memberIdx = #{memberIdx}
